import { useState } from "react";
import "../styles/board.css";
import { Sudoku } from "./Sudoku";

export type Board = {
  num: number;
  box: number;
  row: number;
  col: number;
};

export type BoardProps = {
  originalBoard: number[][];
  initialBoard: number[][];
};

export const colors = [
  "#DAB49D",
  "#FD9E72",
  "#F2D492",
  "#A9C5A0",
  "#9DCBBA",
  "#779FA1",
  "#92817A",
  "#96939B",
  "#BFBFBF",
];

export default function SudokuBoard({
  originalBoard,
  initialBoard,
}: BoardProps) {
  const [showAnswers, setShowAnswers] = useState<boolean>(false);

  console.log(originalBoard);
  console.log(initialBoard);

  return showAnswers ? (
    <Answers board={originalBoard} />
  ) : (
    <>
      <div className="board">
        {initialBoard.map((box, index) => (
          <div className="grid-box" id={index.toString()}>
            {box.map((cell, cellIndex) => (
              <div
                className="cell-item"
                id={cellIndex.toString()}
                style={{ backgroundColor: colors[index] }}
              >
                <input
                  defaultValue={cell != 0 ? cell : ""}
                  onChange={(e) => {
                    let numberValue = Number.parseInt(e.target.value);
                    if (
                      numberValue <= 9 &&
                      numberValue >= 1 &&
                      e.target.value.match(/^\d+$/) != null
                    ) {
                      initialBoard[index][cellIndex] = Number.parseInt(
                        e.target.value
                      );
                    } else if (
                      (numberValue >= 9 ||
                        e.target.value.match(/^\d+$/) == null) &&
                      e.target.value.match(/\b/) != null &&
                      initialBoard[index][cellIndex] != 0
                    ) {
                      e.target.value =
                        initialBoard[index][cellIndex].toString();
                    } else {
                      e.target.value = "";
                    }
                  }}
                  className="input-cell"
                  readOnly={cell != 0}
                  style={cell != 0 ? { cursor: "default" } : {}}
                />
              </div>
            ))}
          </div>
        ))}
      </div>
      <button className="show-answers" onClick={() => setShowAnswers(true)}>
        Show answers
      </button>
    </>
  );
}
