import { Board } from "./TestBoard";

export function CreateBoard(): Board[] {
  let board: Board[] = [];
  /*for (let i = 0; i < 9; i++) {
    board = [...board, ...CreateBox(board, i)] as Board[];
  }
  return board;*/
  return DiagonalBoxes();
}

function DiagonalBoxes(): Board[] {
  let board: Board[] = [];
  for (let i = 0; i < 9; i += 4) {
    let numbers = shuffleArray(
      Array.from(Array(9).keys()).map((num) => num + 1)
    );
    for (let itemNumber = 0; itemNumber < 9; itemNumber++) {
      let sudokuItem = numbers.pop() || 8;
      let rowNumber = Math.floor(itemNumber / 3);
      let colNumber = itemNumber % 3;
      let boxNumber = i;
      board.push({
        box: boxNumber,
        row: rowNumber,
        num: sudokuItem,
        col: colNumber,
      });
    }
  }
  return board;
}

function CreateBox(board: Board[], row: number): Board[] {
  let conflict = true;
  let newRow: Board[] = [];
  while (conflict) {
    let nums = Array.from(Array(9).keys()).map((num) => num + 1);
    let possibleIndices = shuffleArray(nums.map((item) => item - 1));
    for (let i = 0; i < 9; i++) {
      let newItemNum = nums.pop() || 9;
      let colNumber = possibleIndices.pop() || 8;
      let rowNumber = row;
      let boxNumber = Math.floor(colNumber / 3);
      newRow.push({
        num: newItemNum,
        row: rowNumber,
        col: colNumber,
        box: boxNumber,
      });
    }
    conflict = false;
    for (let i = 0; i < 9; i++) {
      if (
        board.filter(
          (item) => item.col == newRow[i].col && item.num == newRow[i].num
        ).length > 0 ||
        board.filter(
          (item) => item.box == newRow[i].box && item.num == newRow[i].num
        ).length > 0
      ) {
        conflict = true;
      }
    }
  }
  return newRow;
}

// From GeeksforGeeks
function shuffleArray(array: any[]): any[] {
  for (var i = array.length - 1; i > 0; i--) {
    var j = Math.floor(Math.random() * (i + 1));

    var temp = array[i];
    array[i] = array[j];
    array[j] = temp;
  }

  return array;
}
