import { Board } from "./TestCode";

export type NewBoard = {
  box: number;
  num: number;
};

// JS program to implement the approach
export class Sudoku {
  N: number;
  SRN: number;
  mat: Board[];
  // Constructor
  constructor(N: number, K: number) {
    this.N = 9;
    // this.K = 40;

    // Compute square root of N
    this.SRN = 3;

    // Initialize all entries as false to indicate
    // that there are no edges initially
    let sampleBoardItem: Board = {
      row: 0,
      col: 0,
      box: 0,
      num: 0,
    };
    this.mat = Array.from(
      {
        length: N * N,
      },
      () => sampleBoardItem
    );
  }

  // Sudoku Generator
  fillValues() {
    // Fill the diagonal of SRN x SRN matrices
    this.fillDiagonal();

    // Fill remaining blocks
    // this.fillRemaining(0, this.SRN);
  }

  // Fill the diagonal SRN number of SRN x SRN matrices
  // fillDiagonal() {
  //   for (let i = 0; i < this.N; i += this.SRN) {
  //     // for diagonal box, start coordinates->i==j
  //     this.fillBox(i, i, i);
  //   }
  // }

  DiagonalBoxes() {
    for (let i = 0; i < 9; i += 4) {
      let numbers = shuffleArray(
        Array.from(Array(9).keys()).map((num) => num + 1)
      );
      for (let itemNumber = 0; itemNumber < 9; itemNumber++) {
        let sudokuItem = numbers.pop() || 8;
        let rowNumber = Math.floor(itemNumber / 3);
        let colNumber = itemNumber % 3;
        let boxNumber = i;
        board.push({
          box: boxNumber,
          row: rowNumber,
          num: sudokuItem,
          col: colNumber,
        });
      }
    }
    return board;
  }

  // Returns false if given 3 x 3 block contains num.
  unUsedInBox(rowStart: number, colStart: number, num: number) {
    for (let i = 0; i < this.SRN; i++) {
      for (let j = 0; j < this.SRN; j++) {
        if (this.mat[(rowStart + i) * 9 + colStart + j].num === num) {
          return false;
        }
      }
    }
    return true;
  }

  // Fill a 3 x 3 matrix.
  fillBox(row: number, col: number, box: number) {
    let num = 0;
    for (let i = 0; i < 3; i++) {
      for (let j = 0; j < 3; j++) {
        while (true) {
          num = this.randomGenerator(this.N);
          if (this.unUsedInBox(row, col, num)) {
            break;
          }
        }
        this.mat[(row + i) * 9 + (col + j)] = {
          row: row + i,
          col: col + j,
          box: box,
          num: num,
        };
      }
    }
  }

  // Random generator
  randomGenerator(num: number) {
    return Math.floor(Math.random() * num + 1);
  }

  // Check if safe to put in cell
  checkIfSafe(i: number, j: number, num: number) {
    return (
      this.unUsedInRow(i, num) &&
      this.unUsedInCol(j, num) &&
      this.unUsedInBox(i - (i % this.SRN), j - (j % this.SRN), num)
    );
  }

  // check in the row for existence
  unUsedInRow(i: number, num: number) {
    for (let j = 0; j < this.N; j++) {
      if (this.mat[i * 9 + j].num === num) {
        return false;
      }
    }
    return true;
  }

  // check in the row for existence
  unUsedInCol(j: number, num: number) {
    for (let i = 0; i < this.N; i++) {
      if (this.mat[i * 9 + j].num === num) {
        return false;
      }
    }
    return true;
  }

  // A recursive function to fill remaining
  // matrix
  fillRemaining(i: number, j: number): boolean {
    // Check if we have reached the end of the matrix
    if (i === this.N - 1 && j === this.N) {
      return true;
    }

    // Move to the next row if we have reached the end of the current row
    if (j === this.N) {
      i += 1;
      j = 0;
    }

    // Skip cells that are already filled
    if (this.mat[i * 9 + j].num !== 0) {
      return this.fillRemaining(i, j + 1);
    }

    // Try filling the current cell with a valid value
    for (let num = 1; num <= this.N; num++) {
      if (this.checkIfSafe(i, j, num)) {
        this.mat[i * 9 + j] = {
          row: i,
          col: j,
          box: i - (i % 3) + j - (j % 3),
          num: num,
        };
        if (this.fillRemaining(i, j + 1)) {
          return true;
        }
        this.mat[i * 9 + j] = {
          row: 0,
          col: 0,
          box: 0,
          num: 0,
        };
      }
    }

    // No valid value was found, so backtrack
    return false;
  }

