  //         let j = Math.floor(Math.random() * this.N);
  //         if (this.mat[i][j] !== 0) {
  //             count--;
  //             this.mat[i][j] = 0;
  //         }
  //     }

  //     return;
  // }
}

// // Driver code
// let N = 9
// let K = 40
// let sudoku = new Sudoku(N, K)
// sudoku.fillValues()
// sudoku.printSudoku()

// This code is contributed by phasing17.

function shuffleArray(array: any[]): any[] {
  for (var i = array.length - 1; i > 0; i--) {
    var j = Math.floor(Math.random() * (i + 1));

    var temp = array[i];
    array[i] = array[j];
    array[j] = temp;
  }

  return array;
}
