import "../styles/board.css";

type Board = {
  num: number;
  box: number;
  row: number;
  col: number;
};

export default function TestBoard() {
  const colors = [
    "#DAB49D",
    "#F29559",
    "#F2D492",
    "#A9C5A0",
    "#9DCBBA",
    "#779FA1",
    "#92817A",
    "#979B8D",
    "#F1DABF", //#DAB49D pink
  ];

  let board: Board[] = [];
  let initial: number[][] = [];
  for (let i = 0; i < 81; i += 1) {
    let numberOptions = [1, 2, 3, 4, 5, 6, 7, 8, 9];
    let rowNumber = Math.floor(i / 9);
    let colNumber = i % 9;
    let boxNumber = ((i - (i % 3)) / 3) % 9;
    let index = 5;
    let conflict = true;
    let tries = 0;
    while (tries < 15) {
      index = Math.floor(Math.random() * 8 + 1);
      conflict =
        board.filter((item) => item.row == rowNumber && item.num == index)
          .length > 0 ||
        board.filter((item) => item.col == colNumber && item.num == index)
          .length > 0 ||
        board.filter((item) => item.box == boxNumber && item.num == index)
          .length > 0;
      if (!conflict) {
        break;
      }
      tries++;
    }
    if (!conflict) {
      board.push({
        num: index,
        col: colNumber,
        row: rowNumber,
        box: boxNumber,
      });
    } else {
      board.push({
        num: 0,
        col: colNumber,
        row: rowNumber,
        box: boxNumber,
      });
    }
  }
  [0, 1, 2, 3, 4, 5, 6, 7, 8].map((value) => {
    let box = board.filter((item) => item.box == value).map((x) => x.num);
    initial.push(box);
  });
  return (
    <div className="board">
      {initial.map((box, index) => {
        return (
          <div className="grid-box" id={index.toString()}>
            {box.map((cell, cellIndex) => {
              return (
                <div
                  className="cell-item"
                  id={cellIndex.toString()}
                  style={{ backgroundColor: colors[index] }}
                >
                  <input
                    defaultValue={cell != 0 ? cell : ""}
                    onChange={(e) => {
                      let numberValue = Number.parseInt(e.target.value);
                      if (numberValue && numberValue <= 9 && numberValue >= 1) {
                        initial[index][cellIndex] = Number.parseInt(
                          e.target.value
                        );
                      } else {
                        e.target.value = "";
                      }
                    }}
                    className="input-cell"
                    contentEditable={cell == 0}
                  />
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}
