import { useState } from "react";
import "../styles/board.css";
import { Sudoku } from "./Sudoku";

export type Board = {
  num: number;
  box: number;
  row: number;
  col: number;
};

export type BoardProps = {
  initialBoard: number[][];
  originalBoard: number[][];
};

export default function SudokuBoard() {
  const [showAnswers, setShowAnswers] = useState<boolean>(false);
  const colors = [
    "#DAB49D",
    "#FD9E72",
    "#F2D492",
    "#A9C5A0",
    "#9DCBBA",
    "#779FA1",
    "#92817A",
    "#96939B",
    "#BFBFBF",
  ];

  let sudoku = new Sudoku();
  sudoku.fillValues();
  console.log(sudoku.mat);
  let board: number[][] = [];
  [0, 1, 2, 3, 4, 5, 6, 7, 8].map((value) => {
    let box = sudoku.mat.filter((item) => item.box == value).map((x) => x.num);
    board.push(box);
  });

  let initial: number[][] = [];
  [0, 1, 2, 3, 4, 5, 6, 7, 8].map((value) => {
    let box = sudoku.mat.filter((item) => item.box == value).map((x) => x.num);
    initial.push(box);
  });
  for (let x = 0; x < 9; x++) {
    for (let y = 0; y < 9; y++) {
      let number = Math.floor(Math.random() * 2);
      if (number != 1) {
        initial[x][y] = 0;
      }
    }
  }

  return showAnswers ? (
    <div className="board">
      {board.map((box, index) => (
        <div className="grid-box">
          {box.map((cell, cellIndex) => (
            <div
              className="cell-item"
              style={{ backgroundColor: colors[index] }}
            >
              <input
                defaultValue={cell}
                className="input-cell"
                readOnly={true}
                style={{ cursor: "default" }}
              />
            </div>
          ))}
        </div>
      ))}
    </div>
  ) : (
    <>
      <div className="board">
        {initial.map((box, index) => (
          <div className="grid-box" id={index.toString()}>
            {box.map((cell, cellIndex) => (
              <div
                className="cell-item"
                id={cellIndex.toString()}
                style={{ backgroundColor: colors[index] }}
              >
                <input
                  defaultValue={cell != 0 ? cell : ""}
                  onChange={(e) => {
                    let numberValue = Number.parseInt(e.target.value);
                    if (
                      numberValue <= 9 &&
                      numberValue >= 1 &&
                      e.target.value.match(/^\d+$/) != null
                    ) {
                      initial[index][cellIndex] = Number.parseInt(
                        e.target.value
                      );
                    } else if (
                      (numberValue >= 9 ||
                        e.target.value.match(/^\d+$/) == null) &&
                      e.target.value.match(/\b/) != null &&
                      initial[index][cellIndex] != 0
                    ) {
                      e.target.value = initial[index][cellIndex].toString();
                    } else {
                      e.target.value = "";
                    }
                  }}
                  className="input-cell"
                  readOnly={cell != 0}
                  style={cell != 0 ? { cursor: "default" } : {}}
                />
              </div>
            ))}
          </div>
        ))}
      </div>
      <button className="show-answers" onClick={() => setShowAnswers(true)}>
        Show answers
      </button>
    </>
  );
}
