import "../styles/board.css";

type Board = {
  num: number;
  box: number;
  row: number;
  col: number;
};

export default function Board() {
  const colors = [
    "#DAB49D",
    "#F29559",
    "#F2D492",
    "#A9C5A0",
    "#9DCBBA",
    "#779FA1",
    "#92817A",
    "#979B8D",
    "#F1DABF", //#DAB49D pink
  ];

  let board: Board[] = [];
  let initial: number[][] = [];
  let boxNumber = 0;
  let rowNumber = 0;
  let colNumber = 0;
  let index = Math.floor(Math.random() * 8 + 1);
  for (let i = 0; i < 81; i++) {
    let numberOptions = [1, 2, 3, 4, 5, 6, 7, 8, 9];
    rowNumber = Math.floor(i / 9);
    colNumber = i % 9;
    boxNumber = i - Math.floor(i / 9) * 9;
    let conflict = true;
    while (conflict) {
      index = numberOptions.pop() || 1;
      conflict =
        board.filter(
          (item) =>
            (item.row == rowNumber && item.num == index) ||
            (item.col == colNumber && item.num == index) ||
            (item.box == boxNumber && item.num == index)
        ).length > 0;
    }
    board.push({
      num: index,
      col: colNumber,
      row: rowNumber,
      box: boxNumber,
    });
  }
  console.log(board);
  console.log(board.filter((item) => item.box == 1));
  [0, 1, 2, 3, 4, 5, 6, 7, 8].map((value) => {
    let box = board.filter((item) => item.box == value).map((x) => x.num);
    initial.push(box);
  });
  for (let x = 0; x < 9; x++) {
    for (let y = 0; y < 9; y++) {
      let number = Math.floor(Math.random() * 9);
      if (number != 1) {
        initial[x][y] = 0;
      }
    }
  }
  console.log(initial);
  return (
    <div className="board">
      {initial.map((box, index) => {
        return (
          <div className="grid-box" id={index.toString()}>
            {box.map((cell, cellIndex) => {
              return (
                <div
                  className="cell-item"
                  id={cellIndex.toString()}
                  style={{ backgroundColor: colors[index] }}
                >
                  <input
                    defaultValue={cell != 0 ? cell : ""}
                    onChange={(e) => {
                      let numberValue = Number.parseInt(e.target.value);
                      if (numberValue && numberValue <= 9 && numberValue >= 1) {
                        initial[index][cellIndex] = Number.parseInt(
                          e.target.value
                        );
                      } else {
                        e.target.value = "";
                      }
                    }}
                    className="input-cell"
                    contentEditable={cell == 0}
                  />
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}
