import { isContentEditable } from "@testing-library/user-event/dist/utils";
import "../styles/board.css";

export default function Board() {
  let board: number[][] = [];
  for (let i = 0; i < 3; i++) {
    let box: number[] = [];
    for (let j = 0; j < 9; j++) {
      let defaultValue = Math.round(Math.random() * 3);
      if (defaultValue == 1) {
        let violatingRules = true;
        let defaultNumber = 0;
        while (violatingRules) {
          defaultNumber = Math.round(Math.random() * 8 + 1);
          let violatingBox = box.includes(defaultNumber);
          let violatingRow = false,
            violatingCol = false;
          let col_offset = j % 3;
          let row_offset = Math.floor(j / 3);
          for (let x = 0; x < i; x++) {
            for (let y = 0; y < j; y++) {
              if (
                Math.floor(y / 3) == row_offset &&
                defaultNumber == board[x][y]
              ) {
                violatingRow = true;
              }
              if (y % 3 == col_offset && defaultNumber == board[x][y]) {
                violatingCol = true;
              }
            }
          }
          violatingRules = violatingBox || violatingRow || violatingCol;
          if (!violatingRules) {
            console.log("num:" + defaultNumber);
            console.log("box:" + i);
            console.log("item:" + j);
            console.log("coloff:" + col_offset);
            console.log("rowoff:" + row_offset);
          }
        }
        box.push(defaultNumber);
      } else {
        box.push(0);
      }
      console.log("--");
    }
    board.push(box);
  }
  console.log(board);
  return (
    <div className="board">
      {board.map((box, index) => {
        return (
          <div className="grid-box" id={index.toString()}>
            {box.map((cell, cellIndex) => {
              return (
                <div className="cell-item" id={cellIndex.toString()}>
                  <p style={{ position: "fixed", fontSize: "0.2rem" }}>
                    {cellIndex}
                  </p>
                  <input
                    defaultValue={cell != 0 ? cell : ""}
                    onChange={(e) => {
                      let numberValue = Number.parseInt(e.target.value);
                      if (numberValue && numberValue <= 9 && numberValue >= 1) {
                        board[index][cellIndex] = Number.parseInt(
                          e.target.value
                        );
                      } else {
                        e.target.value = "";
                      }
                    }}
                    className="input-cell"
                    contentEditable={cell == 0}
                    onClick={() => {
                      console.log(index);
                      console.log(cellIndex);
                    }}
                  />
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}
