import { useState } from "react";
import "../styles/board.css";
import { NewBoard, Sudoku } from "./Sudoku";

export type Board = {
  num: number;
  box: number;
  row: number;
  col: number;
};

export default function TestBoard() {
  const [showAnswers, setShowAnswers] = useState<boolean>(false);
  const colors = [
    "#DAB49D",
    "#FD9E72",
    "#F2D492",
    "#A9C5A0",
    "#9DCBBA",
    "#779FA1",
    "#92817A",
    "#96939B",
    "#BFBFBF",
  ];

  let sudoku = new Sudoku();
  sudoku.fillValues();
  console.log(sudoku.mat);
  let board: NewBoard[][] = Array.from(
    {
      length: 9,
    },
    () =>
      Array.from({
        length: 9,
      })
  );
  sudoku.mat.map(
    (item, index) => (board[Math.floor(index / 9)][index % 9] = item)
  );

  let initial: number[][] = [];
  [0, 1, 2, 3, 4, 5, 6, 7, 8].map((value) => {
    let box = sudoku.mat.filter((item) => item.box == value).map((x) => x.num);
    initial.push(box);
  });
  for (let x = 0; x < 9; x++) {
    for (let y = 0; y < 9; y++) {
      let number = Math.floor(Math.random() * 2);
      if (number != 1) {
        initial[x][y] = 0;
      }
    }
  }

  return;
  showAnswers ? (
    <div className="board"></div>
  ) : (
    <div className="board">
      {initial.map((box, index) => {
        return (
          <div className="grid-box" id={index.toString()}>
            {box.map((cell, cellIndex) => {
              return (
                <div
                  className="cell-item"
                  id={cellIndex.toString()}
                  style={{ backgroundColor: colors[index] }}
                >
                  <input
                    defaultValue={cell != 0 ? cell : ""}
                    onChange={(e) => {
                      let numberValue = Number.parseInt(e.target.value);
                      if (numberValue && numberValue <= 9 && numberValue >= 1) {
                        initial[index][cellIndex] = Number.parseInt(
                          e.target.value
                        );
                      } else {
                        e.target.value = "";
                      }
                    }}
                    className="input-cell"
                    readOnly={cell != 0}
                  />
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}
