import "../styles/board.css";

type Board = {
  num: number;
  box: number;
  row: number;
  col: number;
};

export default function Board() {
  const colors = [
    "#DAB49D",
    "#F29559",
    "#F2D492",
    "#A9C5A0",
    "#9DCBBA",
    "#779FA1",
    "#92817A",
    "#979B8D",
    "#F1DABF", //#DAB49D pink
  ];

  let board: Board[] = [];
  let numberOptions = [1, 2, 3, 4, 5, 6, 7, 8, 9];
  let initial: number[][] = [];
  let boxNumber = 0;
  let rowNumber = 0;
  let colNumber = 0;
  let index = Math.floor(Math.random() * 8);
  let rowConflict = false,
    boxConflict = false,
    colConflict = false;
  for (let i = 0; i < 81; i++) {
    rowNumber = Math.floor(i / 9);
    colNumber = i % 3;
    boxNumber++;
    while (rowConflict || colConflict || boxConflict) {
      rowConflict =
        board.filter(
          (item) => item.row == rowNumber && item.num == numberOptions[index]
        ).length > 0;
      colConflict =
        board.filter(
          (item) => item.col == colNumber && item.num == numberOptions[index]
        ).length > 0;
      boxConflict =
        board.filter(
          (item) => item.box == boxNumber && item.num == numberOptions[index]
        ).length > 0;
      index = Math.floor(Math.random() * 8);
    }
    board.push(numberOptions[index]);
  }
  return (
    <div className="board">
      {initial.map((box, index) => {
        return (
          <div className="grid-box" id={index.toString()}>
            {box.map((cell, cellIndex) => {
              return (
                <div
                  className="cell-item"
                  id={cellIndex.toString()}
                  style={{ backgroundColor: colors[index] }}
                >
                  <input
                    defaultValue={cell != 0 ? cell : ""}
                    onChange={(e) => {
                      let numberValue = Number.parseInt(e.target.value);
                      if (numberValue && numberValue <= 9 && numberValue >= 1) {
                        initial[index][cellIndex] = Number.parseInt(
                          e.target.value
                        );
                      } else {
                        e.target.value = "";
                      }
                    }}
                    className="input-cell"
                    contentEditable={cell == 0}
                  />
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}
