import "../styles/board.css";

export default function Board() {
  const colors = [
    "#FFAAEA",
    "#F29559",
    "#F2D492",
    "#A9C5A0",
    "#9DCBBA",
    "#779FA1",
    "#92817A",
    "#979B8D",
    "#F1DABF", //#DAB49D pink
  ];

  let board: number[][] = [];
  for (let i = 0; i < 9; i++) {
    let box: number[] = [];
    for (let j = 0; j < 9; j++) {
      let defaultValue = Math.round(Math.random() * 3);
      if (defaultValue == 1) {
        let violatingRules = true;
        let defaultNumber = 0;
        while (violatingRules) {
          defaultNumber = Math.round(Math.random() * 8 + 1);
          let violatingBox = box.includes(defaultNumber);
          let violatingRow = false,
            violatingCol = false;
          for (
            let x = Math.floor(i / 3) * 3;
            x < Math.min(Math.floor(i / 3) * 3 + 3, i);
            x++
          ) {
            for (
              let y = Math.floor(j / 3) * 3;
              y < Math.floor(j / 3) * 3 + 3;
              y++
            ) {
              if (board[x][y] == defaultNumber) {
                violatingRow = true;
              }
            }
          }

          for (let x = i % 3; x < i; x += 3) {
            for (let y = j % 3; y < 9; y += 3) {
              if (board[x][y] == defaultNumber) {
                violatingCol = true;
              }
            }
          }
          violatingRules = violatingBox || violatingRow || violatingCol;
        }
        box.push(defaultNumber);
      } else {
        box.push(0);
      }
    }
    board.push(box);
  }
  return (
    <div className="board">
      {board.map((box, index) => {
        return (
          <div className="grid-box" id={index.toString()}>
            {box.map((cell, cellIndex) => {
              return (
                <div
                  className="cell-item"
                  id={cellIndex.toString()}
                  style={{ backgroundColor: colors[index] }}
                >
                  <input
                    defaultValue={cell != 0 ? cell : ""}
                    onChange={(e) => {
                      let numberValue = Number.parseInt(e.target.value);
                      if (numberValue && numberValue <= 9 && numberValue >= 1) {
                        board[index][cellIndex] = Number.parseInt(
                          e.target.value
                        );
                      } else {
                        e.target.value = "";
                      }
                    }}
                    className="input-cell"
                    contentEditable={cell == 0}
                  />
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}
